<script>
import { render as renderElem } from '@goodt-wcore/render';

export default {
    props: {
        elem: {
            type: Object
        },
        dataAddons: {
            type: [Object, Function],
            default() {
                return {};
            }
        }
    },
    render(h) {
        const { elem, dataAddons } = this;
        const { isEditorMode } = this.$root;
        return renderElem(h, elem, dataAddons, isEditorMode);
    }
};
</script>
