const { bem, tag } = require('@goodt/posthtml-config');
const PostHtmlRuleFactory = (lang, loaderOptions) => ({
  test: new RegExp(`${lang}`),
  use: [
    /* config.module.rule('posthtml').use('posthtml-loader') */
    {
      loader: 'posthtml-loader',
      options: loaderOptions
    }
  ]
});

module.exports.PostHtmlRules = [
  PostHtmlRuleFactory('posthtml-bem', bem),
  PostHtmlRuleFactory('posthtml-tag', tag)
];
