module.exports = (config) => {
  const chainWebpack = require('./chainWebpack').build(config);
  const configureWebpack = require('./configureWebpack').build(config);
  const vueConfig = require('./vue.config.js').build(config);
  return {
    vueConfig: { ...vueConfig, chainWebpack, configureWebpack },
    // return separate for back compatibility with previous editor/player versions 2.6
    chainWebpack,
    configureWebpack,
  };
}
