const fs = require('fs');
const browserify = require('browserify');

[
    ['./dist/all.css', './docs/css/all.css'],
    ['./dist/theme.css', './docs/css/theme.css']
].forEach(([inp, out]) => fs.copyFileSync(inp, out));

[
    ['./scripts/theme-color.js', './docs/_assets/js/theme-color.js', 'Theme'],
    ['./scripts/theme-parser.js', './docs/_assets/js/theme-parser.js', 'ThemeParser']
].forEach(([inp, out, name]) => {
    browserify(inp, { standalone: name }).bundle().pipe(fs.createWriteStream(out));
});
