const { resolve } = require('path');
const { build } = require('./theme-utils');
const theme = require('./theme');
const args = process.argv.slice(2);

if (!args.length) {
    console.log(`Usage node build-theme.js <path>`);
    process.exit();
}
const outputPath = resolve(args[0]);

build(theme, outputPath);
console.log(`theme build params: ${outputPath}`);
