const color = require('color');

const ColorVariant = {
    light: c => color(c).lighten(0.35).hex(),
    lighter: c => color(c).lighten(0.6).hex(),
    dark: c => color(c).darken(0.35).hex(),
    darker: c => color(c).darken(0.6).hex(),
    focus: c => color(c).alpha(0.3).hexa(),
    hover: c => color(c).darken(0.1).hex()
};

module.exports = { ColorVariant };
