const { join } = require('path');
const { dependencies, aliases } = require('./package.json');

// prettier-ignore
const PackageAliases = Object.entries({ ...dependencies, ...aliases })
    .filter(([, value]) => value.startsWith('file:'))
    .reduce(
        (acc, [name, value]) => ({
            ...acc,
            [name]: join('goodt-wcore', value.replace('file:', ''))
        }), {});

module.exports = PackageAliases;
