export enum AdapterLoginStrategyType {
    CREDENTIALS = 'credentials',
    REDIRECT = 'redirect'
}

export enum AdapterLogoutStrategyType {
    CREDENTIALS = 'credentials',
    REDIRECT = 'redirect'
}

export interface IAdapterLoginStrategy {
    type: (typeof AdapterLoginStrategyType)[keyof typeof AdapterLoginStrategyType];
    options?: { url: string };
}

export interface IAdapterLogoutStrategy {
    type: (typeof AdapterLogoutStrategyType)[keyof typeof AdapterLogoutStrategyType];
    options?: { url: string };
}
