<template>
    <div class="node">
        <div class="node__content" :class="{ selected: isSelected }" @click="onNodeClick">
            <i class="mdi mdi-folder mdi-18px color-primary"></i>
            <div class="flex-grow pad-left-3 text-truncate" :title="node.name">
                {{ node.name }}
            </div>
            <template v-if="isLoading">
                <span class="preloader"></span>
            </template>
            <template v-else>
                <template v-if="!isFirstFetch && nodes.length">
                    <span class="text-xsmall">({{ nodes.length }})</span>
                </template>
                <template v-if="displayIcons">
                    <i
                        class="mdi mdi-18px color-grey mar-left-3"
                        :class="[isOpen ? 'mdi-chevron-up' : 'mdi-chevron-down']"></i>
                </template>
            </template>
        </div>
        <div class="node__children" v-if="isOpen" :style="{ '--depth': depth + 1 }">
            <ui-folder-node
                v-for="child in nodes"
                v-bind="{ depth: depth + 1, node: child, selectedId, getNodes, selectNode }"
                :key="child.id"></ui-folder-node>
        </div>
    </div>
</template>
<script>
export default {
    name: 'UiFolderNode',
    props: {
        depth: {
            type: Number,
            default: 0
        },
        node: {
            type: Object,
            default: null
        },
        selectedId: {
            type: [Number, String],
            default: ''
        },
        getNodes: {
            type: Function,
            default: (id) => []
        },
        selectNode: {
            type: Function,
            default: (id) => []
        }
    },
    data() {
        return { isOpen: false, isLoading: false, isFirstFetch: true, nodes: [] };
    },
    computed: {
        isSelected() {
            return this.node.id === this.selectedId;
        },
        displayIcons() {
            const { isFirstFetch, nodes } = this;
            return isFirstFetch || nodes.length > 0;
        }
    },
    methods: {
        async onNodeClick() {
            const { id } = this.node;
            this.isOpen = !this.isOpen;
            this.selectNode(id);

            if (this.isOpen && this.isFirstFetch) {
                this.isLoading = true;
                this.nodes = await this.getNodes(id);
                this.isLoading = false;
                this.isFirstFetch = false;
            }
        }
    }
};
</script>
<style lang="less" scoped>
.node {
    --depth-padding: calc(var(--depth, 0) * 1rem);
    &__content {
        display: flex;
        flex-direction: row;
        cursor: pointer;
        user-select: none;
        padding: 0.5rem 1rem;
        padding-left: calc(var(--depth-padding) + 1rem);
        align-items: center;
        transition: background 0.1s ease;
        &:hover,
        &.selected {
            background: var(--color-primary-focus);
        }
    }
    &__children {
        position: relative;
        &:before {
            position: absolute;
            left: calc(var(--depth-padding) + 1.5rem);
            top: 0;
            border-left: 1px solid var(--color-grey-light);
            width: 2px;
            height: 100%;
            content: '';
        }
    }
}
</style>
