<template>
    <div class="node" :style="{ '--depth': depth }">
        <div class="node__content" :class="{ selected: isSelected }" @click="onClick">
            <i class="mdi mdi-file-outline mdi-18px"></i>
            <div class="flex-grow pad-left-3">
                {{ entity.name }}
            </div>
            <ui-entity :type="entityType.name">{{ entityType.title }}</ui-entity>
        </div>
    </div>
</template>
<script>
import UiEntity from '../../shared/EntityBadge.vue';

export default {
    components: { UiEntity },
    props: {
        depth: {
            type: Number,
            default: 0
        },
        entity: {
            type: Object,
            default: null
        },
        selectedEntityId: {
            type: [Number, String],
            default: ''
        },
        selectEntity: {
            type: Function,
            default: () => {}
        }
    },
    computed: {
        isSelected() {
            return this.selectedEntityId === this.entity.id;
        },
        entityType() {
            return this.entity.typeId === 1
                ? { name: 'metric', title: 'метрика' }
                : { name: 'dimension', title: 'измерение' };
        }
    },
    methods: {
        async onClick() {
            this.selectEntity(this.entity);
        }
    }
};
</script>
<style lang="less" scoped>
.node {
    &__content {
        --depth-padding: calc(var(--depth, 0) * 1rem);
        display: flex;
        flex-direction: row;
        cursor: pointer;
        user-select: none;
        padding: 0.5rem 1rem;
        padding-left: calc(var(--depth-padding) + 1rem);
        align-items: center;
        transition: background 0.1s ease;
        &:hover,
        &.selected {
            background: var(--color-primary-focus);
        }
    }
}
</style>
