<template>
    <div class="node">
        <div class="node__content" @click="onNodeClick">
            <i class="mdi mdi-folder mdi-18px color-primary"></i>
            <div class="flex-grow pad-left-3 text-truncate" :title="node.name">
                {{ node.name }}
            </div>
            <template v-if="isLoading">
                <span class="preloader"></span>
            </template>
            <template v-else>
                <template v-if="!isFirstFetch && (nodes.length || entities.length)">
                    <span class="text-xsmall">({{ nodes.length + entities.length }})</span>
                </template>
                <template v-if="displayIcons">
                    <i
                        class="mdi mdi-18px color-grey mar-left-3"
                        :class="[isOpen ? 'mdi-chevron-up' : 'mdi-chevron-down']"></i>
                </template>
            </template>
        </div>
        <div class="node__children" v-if="isOpen" :style="{ '--depth': depth + 1 }">
            <ui-domain-folder-node
                v-for="child in nodes"
                v-bind="{ depth: depth + 1, node: child, selectedEntityId, getNodes, getEntities, selectEntity }"
                :key="child.id"></ui-domain-folder-node>
            <ui-entity-template-node
                v-for="entity in entities"
                v-bind="{ depth: depth + 1, entity, selectedEntityId, selectEntity }"
                :key="entity.id"></ui-entity-template-node>
        </div>
    </div>
</template>
<script>
import UiEntityTemplateNode from './EntityTemplateNode.vue';

export default {
    name: 'UiDomainFolderNode',
    components: { UiEntityTemplateNode },
    props: {
        selectedEntityId: {
            type: [Number, String],
            default: ''
        },
        depth: {
            type: Number,
            default: 0
        },
        node: {
            type: Object,
            default: null
        },
        getNodes: {
            type: Function,
            default: (id) => []
        },
        getEntities: {
            type: Function,
            default: (id) => []
        },
        selectEntity: {
            type: Function,
            default: () => {}
        }
    },
    data() {
        return { isOpen: false, isLoading: false, isFirstFetch: true, nodes: [], entities: [] };
    },
    computed: {
        displayIcons() {
            const { isFirstFetch, nodes } = this;
            return isFirstFetch || nodes.length > 0;
        }
    },
    methods: {
        onNodeClick() {
            const { id } = this.node;
            this.isOpen = !this.isOpen;

            if (this.isOpen && this.isFirstFetch) {
                this.isLoading = true;
                this.isFirstFetch = false;
                Promise.all([this.getNodes(id), this.getEntities(id)])
                    .then(([nodes, entities]) => {
                        this.nodes = nodes;
                        this.entities = entities;
                    })
                    .finally(() => {
                        this.isLoading = false;
                    });
            }
        }
    }
};
</script>
<style lang="less" scoped>
.node {
    --depth-padding: calc(var(--depth, 0) * 1rem);
    &__content {
        display: flex;
        flex-direction: row;
        cursor: pointer;
        user-select: none;
        padding: 0.5rem 1rem;
        padding-left: calc(var(--depth-padding) + 1rem);
        align-items: center;
        transition: background 0.1s ease;
        &:hover {
            background: var(--color-primary-focus);
        }
    }
    &__children {
        position: relative;
        &:before {
            position: absolute;
            left: calc(var(--depth-padding) + 1.5rem);
            top: 0;
            border-left: 1px solid var(--color-grey-light);
            width: 2px;
            height: 100%;
            content: '';
        }
    }
}
</style>
