<template>
    <div class="node">
        <div class="node__content" @click="onDomainClick">
            <i class="mdi mdi-web mdi-18px color-green"></i>
            <div class="flex-grow pad-left-3 text-truncate" :title="project.name">
                {{ project.name }}
            </div>
            <template v-if="isLoading">
                <span class="preloader"></span>
            </template>
            <template v-else>
                <template v-if="!isFirstFetch && folders.length">
                    <span class="text-xsmall">({{ folders.length }})</span>
                </template>
                <template v-if="displayIcons">
                    <i
                        class="mdi mdi-18px color-grey mar-left-3"
                        :class="[isOpen ? 'mdi-chevron-up' : 'mdi-chevron-down']"></i>
                </template>
            </template>
        </div>
        <div v-if="isOpen" :style="{ '--depth': 1 }">
            <ui-project-folder-node
                v-for="folder in folders"
                v-bind="{
                    depth: 1,
                    selectedEntityId,
                    node: folder,
                    getNodes: getFoldersDomain,
                    getEntities,
                    selectEntity
                }"
                :key="folder.id"></ui-project-folder-node>
        </div>
    </div>
</template>
<script>
import UiProjectFolderNode from './ProjectFolderNode.vue';

export default {
    components: { UiProjectFolderNode },
    props: {
        project: {
            type: Object,
            default: null
        },
        selectedEntityId: {
            type: [Number, String],
            default: ''
        },
        selectEntity: {
            type: Function,
            default: () => {}
        },
        getEntities: {
            type: Function,
            default: (id) => []
        },
        getFolders: {
            type: Function,
            default: (id) => []
        }
    },
    data() {
        return { isOpen: false, isLoading: false, isFirstFetch: true, folders: [] };
    },
    computed: {
        displayIcons() {
            const { isFirstFetch, folders } = this;
            return isFirstFetch || folders.length > 0;
        }
    },
    methods: {
        async onDomainClick() {
            const { id } = this.project;
            this.isOpen = !this.isOpen;

            if (this.isOpen && this.isFirstFetch) {
                this.isLoading = true;
                this.isFirstFetch = false;
                this.folders = await this.getFolders(id);
                this.isLoading = false;
            }
        },
        getFoldersDomain(folderId) {
            const { id } = this.project;
            return this.getFolders(id, folderId);
        }
    }
};
</script>
<style lang="less" scoped>
.node {
    &__content {
        display: flex;
        flex-direction: row;
        cursor: pointer;
        user-select: none;
        padding: 0.5rem 1rem;
        align-items: center;
        transition: background 0.1s ease;
        &:hover {
            background: var(--color-primary-focus);
        }
    }
}
</style>
