                <slot></slot>
            </span>
        </template>
        <template #tooltip>
            <div
                class="tooltip color-red bg-white text-xsmall pad-v pad-h-3 radius-1">
                {{ message }}
            </div>
        </template>
    </ui-tooltip>
</template>

<script>
import { Tooltip as UiTooltip } from 'goodteditor-ui';

export default {
    components: {
        UiTooltip
    },

    props: {
        message: {
            type: String
        }
    }
};
</script>

