<template>
    <div class="node">
        <div class="node__content" :class="{ active }" @click="onNodeClick">
            <slot name="icon"></slot>
            <div class="flex-grow">
                <slot></slot>
            </div>
            <i class="mdi mdi-18px color-grey" :class="[isOpen ? 'mdi-chevron-up' : 'mdi-chevron-down']"></i>
        </div>
        <div v-if="isOpen" :style="{ '--depth': depth + 1 }">
            <slot name="children" v-bind="{ depth: depth + 1 }"></slot>
        </div>
    </div>
</template>
<script>
export default {
    props: {
        active: {
            type: Boolean,
            default: false
        },
        depth: {
            type: Number,
            default: 0
        }
    },
    data() {
        return { isOpen: false };
    },
    methods: {
        onNodeClick() {
            this.isOpen = !this.isOpen;
        }
    }
};
</script>
<style lang="less" scoped>
.node {
    &__content {
        --depth-padding: calc(var(--depth, 0) * 1rem);
        display: flex;
        flex-direction: row;
        cursor: pointer;
        user-select: none;
        padding: 0.5rem 1rem;
        padding-left: calc(var(--depth-padding) + 1rem);
        align-items: center;
        transition: background 0.1s ease;
        &:hover,
        &.active {
            background: var(--color-primary-focus);
        }
    }
}
</style>
