<script>
import { Badge } from '@goodt-wcore/panels';
import { EntityType } from "../../const";

const BadgeClassMap = {
    default: ['badge--default'],
    [EntityType.METRIC]: ['badge--metric'],
    [EntityType.DIMENSION]: ['badge--dimension'],
    [EntityType.DRILLDOWN]: ['badge--dimension'],
    [EntityType.FIELD]: ['badge--default'],
    [EntityType.VARIABLE]: ['badge--variable']
};

export default {
    name: 'BadgeDataset',
    extends: Badge,
    computed: {
        cssClass() {
            return BadgeClassMap[this.type];
        }
    }
};
</script>

<style scoped lang="pcss">
@b badge {
    font-size: 0.8rem;
    @m default {
        border: 1px var(--color-muted) solid;
        background-color: transparent;
    }
    @m metric {
        background: var(--color-primary-focus);
    }
    @m dimension {
        background: var(--color-orange-focus);
    }
    @m variable {
        background: var(--color-green-focus);
    }
}
</style>
