import { WidgetMetaComponentOption } from '@goodt-wcore/elem';
import { DATASET_PROP } from '../consts';
import { IDatasetRequestProps } from '../types';
import { IDatasetPanelOptions } from '../panels';

/**
 * @param {{ name:string, dataProviderId:string|number }}
 * @returns {import('./types').IDatasetRequestProps}
 */
export function createDataset({ name: string = '', dataProviderId: string = null } = {}): IDatasetRequestProps;

/**
 *
 * @param {Record<string, any>} meta
 * @param {string} [prop]
 * @param {boolean} [deviations = false]
 * @param {IDatasetPanelOptions|false} [panel={}]
 * @return {Record<string, any>}
 */
export function useDatasetMeta(
    meta: WidgetMetaComponentOption,
    {
        prop = DATASET_PROP,
        deviations = false,
        panel
    }?: {
        prop?: string;
        deviations?: boolean;
        panel?: IDatasetPanelOptions | false;
    }
): WidgetMetaComponentOption;
