/**
 * converts recursively passed value if it contains a css-variable to its computed value
 * @param {*} option
 * @param {CSSStyleDeclaration} computedStyle
 * @return {*}
 */
export function convertCssVarToComputedValue (option: any, computedStyle: CSSStyleDeclaration): any

type SizeInfo = {
    size: string;
    unit?: string;
    value?: string | number;
}

export class SizeConverter {
    /**
     * @private
     * @type {SizeConverter}
     */
    private static _instance: SizeConverter;
    /**
     * @private
     * @type {HTMLDivElement}
     */
    private _gageElement: HTMLDivElement;
    /**
     * @return {SizeConverter}
     */
    static instance: SizeConverter;
    /**
     * Constructor
     *
     * @param {symbol} enforcer  singleton enforcer
     */
    constructor(enforcer: symbol);
    /**
     * @private
     * @return {HTMLDivElement}
     */
    private static _createGageElement(): HTMLDivElement;
    /**
     * @private
     * @param {string} cssVar
     * @param {CSSStyleDeclaration} computedStyle
     * @return {string}
     */
    private static _extractCssVarValue(cssVar: string, computedStyle: CSSStyleDeclaration): string;
    /**
     * converts recursively passed value if it contains a css-variable to its value
     *
     * @param {*} option
     * @param {CSSStyleDeclaration} [computedStyle]
     * @return {*}
     */
    convertCssVarToValue(option: any, computedStyle?: CSSStyleDeclaration): any;
    /**
     * takes size info and returns absolute value expressed in pixels
     *
     * @param {Object} sizeInfo
     * @param {string} [sizeInfo.size=''] unit and value or css-variable
     * @param {string} [sizeInfo.unit] unit
     * @param {string|number} [sizeInfo.value] value
     * @param {CSSStyleDeclaration} [computedStyle] computed style
     * @return {number} unit size in absolute value
     */
    convertToAbsoluteValue(sizeInfo: SizeInfo, computedStyle?: CSSStyleDeclaration): number;
}
