import NumberFormat, { NumberFormatConstructorOptions } from './number-format';
import NumberFormatPattern, { NumberFormatPatternConstructorOptions } from './number-format-pattern';

export interface NumberFormatOptions {
    locale?: string;
}

/**
 * Format number as currency value
 * @param {number} number
 * @param {NumberFormatPatternConstructorOptions} [pattern]
 * @param {NumberFormatOptions} [options]
 * @return {string}
 */
export function formatCurrency(
    number: any,
    { digits, unit, modifiers }?: NumberFormatPatternConstructorOptions,
    { locale }?: NumberFormatOptions
): string;

/**
 * Format number as a decimal number
 * @param {number} number
 * @param {NumberFormatPatternConstructorOptions} [pattern]
 * @param {NumberFormatOptions} [options]
 * @return {string}
 */
export function formatDecimal(
    number: any,
    { digits, unit, modifiers }?: NumberFormatPatternConstructorOptions,
    { locale }?: NumberFormatOptions
): string;

/**
 * Format number according using defined format
 * @param {number} number
 * @param {string|NumberFormat|NumberFormatConstructorOptions} [format]
 * @param {NumberFormatOptions} [options={}]
 * @return {string}
 */
export function formatNumber(
    number: number,
    format?: string | NumberFormat | NumberFormatConstructorOptions,
    options?: NumberFormatOptions
): string;

/**
 * Create format number function
 * @param {string|NumberFormat|NumberFormatConstructorOptions} [format]
 * @param {NumberFormatOptions} [options={}]
 * @return {typeof formatNumber}
 */
export function createFormatNumber({
    format,
    options
}?: {
    format?: string | NumberFormat | NumberFormatConstructorOptions;
    options?: NumberFormatOptions;
}): typeof formatNumber;

export { NumberFormat, NumberFormatPattern };
