import NumberFormatPattern, { NumberFormatPatternConstructorOptions } from './number-format-pattern';

export type TypeEnum = Readonly<{ CURRENCY: 'c'; DECIMAL: 'd' }>;
export type NumberFormatType = TypeEnum['CURRENCY'] | TypeEnum['DECIMAL'];

export type StyleEnum = Readonly<{ CURRENCY: 'currency'; PERCENT: 'percent'; DECIMAL: 'decimal' }>;
export type CurrencyDisplayEnum = Readonly<{ CODE: 'code' }>;
export type NotationEnum = Readonly<{ COMPACT: 'compact'; STANDARD: 'standard' }>;
export type NumberFormatConstructorOptions = { pattern?: NumberFormatPattern | string | NumberFormatPatternConstructorOptions, type?: NumberFormatType };

export default class NumberFormat {
    public pattern: NumberFormatPattern;
    public type: NumberFormatType;

    static Type: TypeEnum;
    static Style: StyleEnum;
    static Notation: NotationEnum;
    static CurrencyDisplay: CurrencyDisplayEnum;

    /**
     * @param {{ pattern?: NumberFormatPattern|string|NumberFormatPatternConstructorOptions, type?: NumberFormatType }} options
     */
    constructor({ pattern, type }?: NumberFormatConstructorOptions);

    /**
     * @return {string}
     */
    toString(): string;

    /**
     * @param {string} formatString
     * @return {NumberFormat}
     */
    static fromString(formatString: string): NumberFormat;
}
