type SpecificationOrCheckFn = Specification | ((value: any) => boolean);

export type Specification = {
    is(value: any): boolean;
    and(specificationOrCheckFn: SpecificationOrCheckFn): Specification;
    or(specificationOrCheckFn: SpecificationOrCheckFn): Specification;
    not(): Specification;
};

export function spec(specificationOrCheckFn: SpecificationOrCheckFn): Specification;
