type AppEntityElem = {
    id: string; // id of element
    pid?: string; // parent id of element
    type: string; // type of element
    props: { [key: string]: any }; // props of element
    visible: boolean; // visibility of element
    children: AppEntityElem[]; // children of element
};

type TMapper<Node, ExtraArgs = IArguments, Result = Node> = (...args: ExtraArgs) => (node: Node) => Result;

export class Queue<Node, Mapper = TMapper<Node>> {
    private _array: Node[];
    private _mapper: Mapper = () => (node: Node) => node;

    constructor(array?: Node[] = [], { mapper }?: { mapper?: Mapper } = {});

    /**
     * @template Node
     * @param {Node[]} nodes - started array of nodes
     * @param {IArguments} args
     */
    enqueue(nodes: Node[], ...args: Parameters<Mapper>): void;

    dequeue(): ReturnType<Mapper>;

    /**
     * @return {boolean} - shows queue is empty or not
     */
    get isEmpty(): boolean;
}

type ChildrenProp = 'children';

/**
 * finds node in the tree by the "match" function and returns it
 * @param {AppEntityElem[]} array
 * @param {string} childrenProp
 * @param {(node: AppEntityElem) => boolean} match
 * @return {Object|null}
 */
export function findNode(
    array: AppEntityElem[],
    childrenProp: ChildrenProp,
    match: (node: AppEntityElem) => boolean
): AppEntityElem | null;

/**
 * find parent of node in the tree by the "match" function and returns it
 * @param {AppEntityElem[]} array
 * @param {string} childrenProp
 * @param {(node: AppEntityElem) => boolean} match
 * @return {Object|null}
 */
export function findParentNode(
    array: AppEntityElem[],
    childrenProp: ChildrenProp,
    match: (node: AppEntityElem) => boolean
): AppEntityElem | null;

/**
 * finds node in the tree by the "match" function, delete it and returns boolean
 * @param {AppEntityElem[]} array
 * @param {string} childrenProp
 * @param {(node: AppEntityElem) => boolean} match
 * @return {boolean}
 */
export function deleteNode(
    array: AppEntityElem[],
    childrenProp: ChildrenProp,
    match: (node: AppEntityElem) => boolean
): boolean;

/**
 * apply callback function for each node in the tree or subtree
 * @param {AppEntityElem[]} array
 * @param {string} childrenProp
 * @param {(node: AppEntityElem, parentNode?: AppEntityElem): void} callback
 */
export function traverse(
    array: AppEntityElem[],
    childrenProp: ChildrenProp,
    callback: (node: AppEntityElem, parentNode?: AppEntityElem) => void
);
