<template functional>
    <div
        key="placeholder"
        :title="`${parent.type} placeholder`"
        :class="{
            [$style.root]: true,
            'cursor-pointer': listeners.click != null
        }"
        v-on="{
            ...listeners.click && {
                click: () => listeners.click($event)
            }
        }">
        <mark :class="$style.mark">{{ (data.attrs && data.attrs.title) || parent.$t(parent.$attrs['data-name'] || parent.type)  }}</mark>
        <code :class="$style.code">
            <div v-if="scopedSlots.default != null">
                <slot></slot>
            </div>
            <div
                v-if="scopedSlots.slot != null"
                title="slot area"
                :class="{
                    [$style.slot]: true,
                    [$style['slot--active']]: injections.hasSlotContent({ parent })
                }">
                <slot name="slot">Slot content</slot>
            </div>
        </code>
    </div>
</template>
<script>
export default {
    inject: {
        hasSlotContent: {
            from: 'hasSlotContent',
            default: () => ({ parent }) => {
                if (parent == null) {
                    return false;
                }
                return parent.getSlotNames().some((slotName) => Boolean(parent.$scopedSlots[slotName]))
            }
        }
    }
};
</script>
<style module lang="less">
.root {
  composes: d-flex flex-col from global;
  min-height: 60px;
}
.code {
  composes: d-flex flex-col flex-grow flex-v-center flex-h-center from global;
  padding: 0.5rem 0.5rem;
  border-top-right-radius: 0;
  border-top-left-radius: 0;

  min-height: 100%;
  gap: 8px;
}
.mark {
  padding: 0.25rem 0.5rem;
  border-bottom-right-radius: 0;
  border-bottom-left-radius: 0;
}
.slot {
  composes: d-flex w-100 pad-v-3 flex-v-center flex-h-center from global;
  border: 1px solid var(--color-muted);
  opacity: 0.4;
  transition: opacity 0.5s;
  background: url("data:image/svg+xml;charset=utf-8,%3Csvg width='8' height='8' viewBox='0 0 6 6' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M5 0h1L0 6V5zm1 5v1H5z' fill='%239C92AC' fill-rule='evenodd'/%3E%3C/svg%3E");
  &--active {
    opacity: 1;
    background: transparent;
    border: none;
  }
  &:hover:not(&--active) {
    opacity: 0.65;
    cursor: copy;
    border: 1px solid var(--color-green);
  }
}
</style>
