export const PropChangeContextType = {
    COMMON: 'common',
    DATASET: 'dataset'
};

export class PropChangeContext {
    public prop: string;
    public type: string;
    public details: Record<string, any>;

    constructor({
        prop,
        type = PropChangeContextType.COMMON,
        details = {}
    }: {
        prop: string;
        type?: string;
        details?: Record<string, any>;
    });
}
