<template>
    <button class="btn btn-small" :class="cssClass" v-bind="$attrs" v-on="$listeners">
        <!--
        @slot label slot
        -->
        <slot />
    </button>
</template>
<script>
export default {
    props: {
        /**
         * Button type
         *
         * @values 'primary', 'secondary', 'ghost', 'error', 'success', 'warn', 'misc'
         */
        type: {
            type: String,
            default: 'primary',
            enum: ['primary', 'secondary', 'ghost', 'error', 'success', 'warn', 'misc']
        },
        /**
         * Whether the button is an 'inline' element or 'block'
         */
        inline: {
            type: Boolean,
            default: false
        },
        /**
         * Only-icon mode (square button)
         */
        icon: {
            type: Boolean,
            default: false
        },
        /**
         * Whether the button has an 'outline' style
         */
        outline: {
            type: Boolean,
            default: false
        }
    },
    computed: {
        cssClass() {
            const { type, inline, icon, outline } = this;
            const classes = [];

            if (type !== '') {
                classes.push(`btn-${type}`);
            }
            if (inline === false) {
                classes.push('d-flex');
            }
            if (icon) {
                classes.push('btn-icon');
            }
            if (outline) {
                classes.push('btn-outline');
            }

            return classes;
        }
    }
};
</script>
