<template>
    <div>
        <control-layout v-bind="{ prop, colSize: 'auto', labelSize: 'small' }" :style="{ 'flex-direction': 'row-reverse' }">
            <template #label="{ Label, getLabel }">
                <label class="text-truncate u-select-none" :for="id">
                    <!--
                    @slot label slot
                    -->
                    <slot v-bind="{ getLabel }">
                        <component :is="Label"></component>
                    </slot>
                </label>
            </template>
            <template #hint="{ getHint }">
                <!--
                @slot hint slot
                -->
                <slot name="hint" v-bind="{ getHint }" />
            </template>
            <template #helper>
                <!--
                @slot helper slot
                -->
                <slot name="helper" />
            </template>
            <template #control>
                <input
                    :id="id"
                    class="checkbox checkbox-small pull-left"
                    :class="controlCl"
                    type="checkbox"
                    v-bind="attrsCb"
                    v-on="listeners"
                />
            </template>
        </control-layout>
    </div>
</template>
<script>
import { ControlMixin } from './utils/index';

let ID = 0;

export default {
    mixins: [ControlMixin],
    props: {
        /** @model */
        value: {
            type: Boolean,
            default: false
        }
    },
    computed: {
        id() {
            return `ui-checkbox-${ID++}`;
        },
        attrsCb() {
            const { value, ...attrs } = this.attrs;
            return { ...attrs, checked: value };
        }
    },
    methods: {
        /**
         * @public
         * @param event
         * @return {*}
         */
        valueExtract: (event) => event.target.checked
    }
};
</script>
