<template>
    <div>
        <control-layout v-bind="{ prop, colSize }">
            <template #label="{ getLabel }">
                <!--
                @slot label slot
                -->
                <slot v-bind="{ getLabel }" />
            </template>
            <template #hint="{ getHint }">
                <!--
                @slot hint slot
                -->
                <slot name="hint" v-bind="{ getHint }"/>
            </template>
            <template #control>
                <div class="form-control form-control-icon-right w-12-12">
                    <input-autocomplete
                        class="w-12-12"
                        :class="controlCl"
                        size="small"
                        v-bind="inputOptions"
                        v-on="listeners"
                    />
                    <div class="icon">
                        <i class="mdi mdi-folder cursor-pointer color-grey" @click="browse" />
                    </div>
                </div>
            </template>
        </control-layout>
    </div>
</template>
<style lang="less" scoped>
.form-control {
    z-index: 0;
}
</style>
<script>
import { InputAutocomplete } from 'goodteditor-ui';
import { FileManager } from '@goodt-wcore/managers';
import { ControlMixin, getConstants } from './utils';

/**
 * environment required
 */
export default {
    components: { InputAutocomplete },
    mixins: [ControlMixin],
    props: {
        /**
         * Suggested options Array[String]
         */
        options: {
            type: Array,
            default() {
                return [];
            }
        }
    },
    computed: {
        inputOptions() {
            const { options = [], ...rest } = this.attrs;
            return { ...rest, options: [...this.options, ...getConstants()] };
        }
    },
    methods: {
        browse() {
            FileManager.instance
                .browse({ selectEnabled: true, selectMultiple: false })
                .then(([file]) => {
                    if (file != null) {
                        const { url } = file;
                        this.onInput(url);
                        this.onChange(url);
                    }
                });
        }
    }
};
</script>
