<template>
    <div>
        <control-layout v-bind="{ prop, colSize }">
            <template #label="{ getLabel }">
                <!--
                @slot label slot
                -->
                <slot v-bind="{ getLabel }" />
            </template>
            <template #hint="{ getHint }">
                <!--
                @slot hint slot
                -->
                <slot name="hint" v-bind="{ getHint }" />
            </template>
            <template #helper>
                <i
                    class="color-mode-icon mdi mdi-palette"
                    :class="[mode === Mode.COLOR ? 'color-primary' : 'color-grey cursor-pointer']"
                    title="Цвета"
                    @click="mode = Mode.COLOR" />
                <i
                    class="color-mode-icon mdi mdi-variable"
                    :class="[mode === Mode.CONSTANT ? 'color-primary' : 'color-grey cursor-pointer']"
                    title="Константы"
                    @click="mode = Mode.CONSTANT" />
                <i
                    class="color-mode-icon mdi mdi-gesture-tap"
                    :class="[mode === Mode.MANUAL ? 'color-primary' : 'color-grey cursor-pointer']"
                    title="Вручную"
                    @click="mode = Mode.MANUAL" />
            </template>
            <template #control>
                <input-color-picker
                    v-if="mode === Mode.MANUAL"
                    class="w-100"
                    :class="controlCl"
                    size="small"
                    v-bind="attrs"
                    v-on="listeners" />
                <ui-select v-else class="w-100" :class="controlCl" size="small" v-bind="selectBinds" v-on="listeners">
                    <template #option:before="{ index, option }">
                        <div
                            v-for="(labelPart, labelIndex) in option.path.slice(0, -1)"
                            v-if="
                                index === 0 || labelPart !== selectBinds.options[index - 1].path[labelIndex]
                            "
                            class="color-constant-label color-constant-label-before"
                            :key="`${index}-${labelIndex}`"
                            :title="labelPart"
                            :style="{
                                paddingLeft: `calc(${labelIndex + 1} * 0.75rem)`,
                                paddingTop: labelIndex === 0 ? 'var(--spacer4)' : 'var(--spacer2)'
                            }">
                            <span class="color-constant-label">{{ labelPart }}</span>
                        </div>
                    </template>
                    <template #option-label="{ label, option }">
                        <div
                            :style="{
                                paddingLeft: `calc(${option.path.length - 1} * 1rem)`
                            }"
                            class="d-flex flex-v-center flex-h-space-between">
                            <span class="color-constant-label" :title="option.path.join(' – ')">{{ label }}</span>
                            <div class="color-preview" :title="option.color" :style="{ background: option.color }"></div>
                        </div>
                    </template>
                    <template #label="{ label, index, option }">
                        <div
                            v-if="index !== -1"
                            class="d-flex flex-v-center flex-h-space-between">
                            <div class="color-constant-label" :title="option.path.join(' – ')">
                                {{ label }}
                            </div>
                            <div class="color-preview" :title="option.color" :style="{ background: option.color }"></div>
                        </div>
                    </template>
                </ui-select>
            </template>
        </control-layout>
    </div>
</template>
<script>
import { Select as UiSelect, InputColorPicker } from 'goodteditor-ui';
import { ColorManager, ConstManager } from '@goodt-wcore/managers';
import { ControlMixin, isValidColor } from './utils';

const Mode = {
    MANUAL: 'manual',
    COLOR: 'color',
    CONSTANT: 'constant'
};

export default {
    components: { InputColorPicker, UiSelect },
    mixins: [ControlMixin],
    static() {
        return {
            Mode,
            ConstOptions: Object.entries(ConstManager.instance.getConstantsHash())
                .filter(([_, constValue]) => isValidColor(constValue))
                .map(([constName, constValue]) => ({
                    label: constName.slice(1, -1),
                    value: constName,
                    color: constValue,
                    path: [constName.slice(1, -1)]
                })),
            ColorOptions: Object.entries(ColorManager.instance.getHash()).map(([, { id, name, value, path }]) => ({
                label: name,
                value: id,
                color: value,
                path
            }))
        };
    },
    data() {
        return {
            mode: Mode.MANUAL
        };
    },
    computed: {
        selectBinds() {
            let options = [];
            let placeholder = '';
            if (this.mode === Mode.CONSTANT) {
                options = this.ConstOptions;
                placeholder = 'Выберите константу';
            }
            if (this.mode === Mode.COLOR) {
                options = this.ColorOptions;
                placeholder = 'Выберите цвет';
            }

            return { ...this.attrs, options, placeholder };
        }
    },
    watch: {
        value: {
            handler(value) {
                if (ColorManager.isConstant(value)) {
                    this.mode = Mode.COLOR;
                    return;
                }
                if (ConstManager.isConstant(value)) {
                    this.mode = Mode.CONSTANT;
                    return;
                }
                this.mode = Mode.MANUAL;
            },
            immediate: true
        }
    },
    methods: {
        isValidColor,
        toggleMode() {
            const modes = Object.values(Mode);
            const nextIndex = (modes.indexOf(this.mode) + 1) % 3;
            this.mode = modes[nextIndex];
        }
    }
};
</script>
<style scoped lang="pcss">
.color-mode-icon {
    line-height: 1;
    float: right;
    margin-right: var(--spacer1);
}

.color-constant-label-before {
    min-height: calc(var(--line-height) * 1rem);
    padding-bottom: var(--spacer2)
}

.color-constant-label,
.color-constant-label__part {
    max-width: 100%;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.color-constant-label {
    max-width: calc(100% - 1rem);

    &__part:not(:first-child) {
        &::before {
            content: '–';
            padding: 0 var(--spacer2);
        }
    }
}

.color-preview {
    width: 1em;
    height: 1em;
    border: 0.0625rem solid var(--color-border);
}
</style>
