<template>
    <div>
        <control-layout v-bind="{ prop, colSize }">
            <template #label="{ getLabel }">
                <!--
                @slot label slot
                -->
                <slot v-bind="{ getLabel }" />
            </template>
            <template #hint="{ getHint }">
                <!--
                @slot hint slot
                -->
                <slot name="hint" v-bind="{ getHint }" />
            </template>
            <template #control>
                <ui-input-tags
                    ref="tags"
                    class="w-12-12"
                    :class="controlCl"
                    size="small"
                    v-bind="attrs"
                    v-on="listeners"
                >
                    <template #tag="{ tag, remove }">
                        <!--
                        @slot tag slot
                        @binding {string} tag           tag
                        @binding {Function} remove      remove tag function(tag:String)
                        @binding {Function} setNewTag   set new tag function(tag:String)
                        -->
                        <slot name="tag" v-bind="{ tag, remove, setNewTag }" />
                    </template>
                </ui-input-tags>
            </template>
        </control-layout>
    </div>
</template>
<script>
import { InputTags as UiInputTags } from 'goodteditor-ui';
import { ControlMixin } from './utils';

export default {
    components: { UiInputTags },
    mixins: [ControlMixin],
    props: {
        /** @model */
        value: {
            type: Array,
            default() {
                return [];
            }
        }
    },
    methods: {
        /**
         * Sest the new tag for the input
         * @param {string} val
         * @public
         */
        setNewTag(val) {
            const el = this.$refs.tags;
            el && el.setNewTag(val);
        }
    }
};
</script>
