<template>
    <div>
        <control-layout v-bind="{ prop, colSize }">
            <template #label="{ getLabel }">
                <!--
                @slot label slot
                -->
                <slot v-bind="{ getLabel }" />
            </template>
            <template #hint="{ getHint }">
                <!--
                @slot hint slot
                -->
                <slot name="hint" v-bind="{ getHint }" />
            </template>
            <template #control>
                <input-time-picker
                    class="w-12-12"
                    :class="controlCl"
                    size="small"
                    v-bind="attrs"
                    v-on="listenersTp"
                />
            </template>
        </control-layout>
    </div>
</template>
<script>
import { InputTimePicker } from 'goodteditor-ui';
import { ControlMixin } from './utils';

/**
 * For more <b>props</b> @see https://goodt-ui.netlify.app/#!/InputDatePicker
 */
export default {
    components: { InputTimePicker },
    mixins: [ControlMixin],
    computed: {
        listenersTp() {
            const { clear, ...rest } = this.listeners;
            return { ...rest, clear: this.onClear };
        }
    },
    methods: {
        onClear(value) {
            /**
             * Change event
             * @property {any} value
             */
            this.$emit('change', '');
        }
    }
};
</script>
