<template>
    <div>
        <ui-has-panel>
            <ui-label :prop="prop" label-size="small">
                <template #default="{ getLabel }">
                    <slot v-bind="{ getLabel }"></slot>
                </template>
                <template #hint="{ getHint }">
                    <slot name="hint" v-bind="{ getHint }"></slot>
                </template>
            </ui-label>
            <template #panel>
                <ui-panel :groups="[{ name: '', slot: 'default' }]">
                    <ui-select class="p" v-model="format.type" :options="Types" @change="saveModel">Тип</ui-select>
                    <ui-has-two-columns class="p">
                        <template #left>
                            <ui-input
                                class="p"
                                type="number"
                                min="0"
                                :max="DIGITS_MAX"
                                v-model.number="format.pattern.digits"
                                @change="saveModel">
                                Точность
                            </ui-input>
                        </template>
                        <template #right>
                            <ui-input class="p" v-model="format.pattern.unit" @change="saveModel">Постфикс</ui-input>
                        </template>
                    </ui-has-two-columns>
                    <ui-switch
                        class="p"
                        v-for="{ label, value } in Modifiers"
                        :key="value"
                        :value="hasModifier(value)"
                        @change="toggleModifier(value)">
                        {{ label }}
                    </ui-switch>
                    <ui-input class="p" type="text" v-model.number="number" pattern="[0-9.]*">Демо</ui-input>
                    <pre class="text-xsmall">{{ numberFormatted }}</pre>
                </ui-panel>
            </template>
        </ui-has-panel>
    </div>
</template>
<script>
import { formatNumber, NumberFormat, NumberFormatPattern } from '@goodt-common/utils-number';
import { ControlMixin } from './utils/index';
import UiInput from './Input.vue';
import UiHasPanel from './HasPanel.vue';
import UiHasTwoColumns from './HasTwoColumns.vue';
import UiSelect from './Select.vue';
import UiSwitch from './Switch.vue';
import UiPanel from './Panel.vue';
import UiLabel from './Label.vue';

export default {
    components: { UiInput, UiHasPanel, UiHasTwoColumns, UiSelect, UiSwitch, UiPanel, UiLabel },
    mixins: [ControlMixin],
    static() {
        const { Type } = NumberFormat;
        const { Modifier, DIGITS_MAX } = NumberFormatPattern;

        return {
            Modifier,
            Types: [
                { value: Type.DECIMAL, label: 'Число' },
                { value: Type.CURRENCY, label: 'Валюта' }
            ],
            Modifiers: [
                { value: Modifier.FIXED, label: 'Скрывать нули после запятой' },
                { value: Modifier.PERCENT, label: 'Режим проценты' },
                { value: Modifier.COMPACT, label: 'Компактный режим' }
            ],
            DIGITS_MAX
        };
    },
    props: {
        /**
         * @model
         */
        value: {
            type: String,
            default: ''
        },
        formatDefault: {
            type: String,
            default: ''
        },
        numberDefault: {
            type: Number,
            default: 12345.6789
        },
    },
    data: (vm) => ({
        /** @type {NumberFormat} */
        format: null,
        number: vm.numberDefault
    }),
    computed: {
        numberFormatted() {
            return formatNumber(this.number, this.format);
        },
        numberFormatDefault() {
            try {
                return NumberFormat.fromString(this.formatDefault).toString();
            } catch {
                return new NumberFormat().toString()
            }
        }
    },
    watch: {
        valueResolved: {
            handler(val) {
                try {
                    this.format = NumberFormat.fromString(val);
                } catch (e) {
                    this.format = NumberFormat.fromString(this.numberFormatDefault);
                }
            },
            immediate: true
        }
    },
    methods: {
        saveModel() {
            let value = this.format.toString();
            if (value === this.numberFormatDefault) {
                value = '';
            }
            this.$emit('input', value);
            this.$emit('change', value);
        },
        /**
         * @param {string} modifier
         * @return {boolean}
         */
        hasModifier(modifier) {
            const { Modifier } = NumberFormatPattern;
            const hasModifier = this.format.pattern.modifiers.includes(modifier);
            return modifier === Modifier.FIXED ? !hasModifier : hasModifier;
        },
        /**
         * @param {string} modifier
         */
        toggleModifier(modifier) {
            const { modifiers } = this.format.pattern;
            const i = modifiers.indexOf(modifier);
            if (i >= 0) {
                modifiers.splice(i, 1);
            } else {
                modifiers.push(modifier);
            }
            this.saveModel();
        }
    },
    setPropsDefault(propsDefault = {}) {
        const { props } = this;
        Object.entries(propsDefault).forEach(([prop, value]) => {
            props[prop].default = value
        });
    }
};
</script>
