<template>
    <div class="d-flex flex-nowrap flex-v-center flex-h-space-between">
        <button
            v-for="({ value, label, icon }, index) in options"
            :key="index"
            :title="label"
            class="btn btn-icon"
            :class="resolveClass(value)"
            @click="onClick(value)">
            <span class="icon">
                <i class="mdi mdi-account mdi-18px" :class="icon" />
            </span>
        </button>
    </div>
</template>
<script>
import { ControlMixin } from './utils';

export default {
    mixins: [ControlMixin],
    props: {
        /**
         * Options Array[Object] ~ [ { value: '', label: '', icon: '' } ]
         */
        options: {
            type: Array,
            default: () => []
        }
    },
    computed: {
        colSizeCl() {
            return `col-${this.colSize}`;
        }
    },
    methods: {
        onClick(value) {
            this.$emit('input', value);
            this.$emit('change', value);
        },
        resolveClass(value) {
            return value === this.valueResolved ? 'btn-primary' : 'btn-ghost';
        }
    }
};
</script>
