<template>
    <div class="row row-hgap-3">
        <div class="col col-vmid text-truncate">
            <ui-label :prop="prop" :label-size="labelSize">
                <template #default="{ Label, getLabel }">
                    <slot name="label" v-bind="{ Label, getLabel }" />
                </template>
                <template #hint="{ Hint, getHint = () => {} }">
                    <slot name="hint" v-bind="{ Hint, getHint }" />
                </template>
            </ui-label>
        </div>
        <div v-if="$slots.helper" class="col col-auto col-vmid">
            <slot name="helper" />
        </div>
        <div class="col col-vmid" :class="[colSizeCl]">
            <slot name="control" />
        </div>
    </div>
</template>
<script>
export default {
    components: {
        UiLabel: () => import('../Label.vue')
    },
    props: {
        prop: {
            type: String,
            default: ''
        },
        colSize: {
            type: String,
            default: '12-12'
        },
        labelSize: {
            type: String,
            default: 'xsmall',
            validation(val) {
                return ['xsmall', 'small'].includes(val);
            }
        }
    },
    computed: {
        colSizeCl() {
            return `col-${this.colSize}`;
        }
    }
};
</script>
