import { ControlMixin } from './ControlMixin';
import { ObjectControlMixin } from './ObjectControlMixin';
import { LabelMixin } from './LabelMixin';

interface IUnknownProps {
    [key: string]: any;
}

interface IFontProps extends IUnknownProps {
    color: string;
    fontSize: string;
    fontFamily: string;
    fontWeight: string;
}

interface IBorderProps extends IUnknownProps {
    color: string;
    width: string;
    style: string;
}

interface IBorderRadiusProps extends IUnknownProps {
    topLeft: string;
    topRight: string;
    bottomLeft: string;
    bottomRight: string;
}

interface IShadowProps extends IUnknownProps {
    color: string;
    blur: string;
    shiftX: string;
    shiftY: string;
    type: string;
}

interface IDimensionsColor {
    color: string,
    step: number,
    colors: Record<string, string>,
    dimensions: string[]
}

interface ICustomizableColor extends IUnknownProps {
    type: string;
    dimensions: IDimensionsColor;
    metrics: { metric: string };
    indexes: { colors: string[] };
}

interface IPaginationButtonStateProps {
    background: string;
    font: IFontProps;
}

interface IPaginationProps extends IUnknownProps {
    active: IPaginationButtonStateProps;
    bas: IPaginationButtonStateProps;
    height: string;
    width: string;
    position: string;
    gap: string;
    pages: number;
    borderRadius: IBorderRadiusProps;
}

function getConstants(): string[];
function getConstantsHash(): Record<string, any>;

function isConstant(value?: string): boolean;

function createFont({
    color = '',
    fontSize = '',
    fontFamily = '',
    fontWeight = '',
    ...rest
}: Partial<IFontProps> = {}): IFontProps;

function createBorder({
    color = 'rgba(238, 238, 238, 1)',
    width = '1px',
    style = 'none',
    ...rest
}: Partial<IBorderProps> = {}): IBorderProps;

function createBorderRadius({
    topLeft = '',
    topRight = '',
    bottomRight = '',
    bottomLeft = '',
    ...rest
}: Partial<IBorderRadiusProps> = {}): IBorderRadiusProps;

function createShadow({
    color = '',
    blur = '',
    shiftX = '',
    shiftY = '',
    type = '',
    ...rest
}: Partial<IShadowProps> = {}): IShadowProps;

function createDimensionsColorSettings({
    color = '',
    step = 0,
    colors = {},
    dimensions = []
}: Partial<IDimensionsColor> = {}): IDimensionsColor;

function createIndexesColorSettings({ colors = [] }: { colors: string[] } = {}): { colors: string[] };

function createMetricsColorSettings({ metric = '' }: { metric: string } = {}): { metric: string };

function createCustomizableColor({
    type = '',
    dimensions = createDimensionsColorSettings(),
    metrics = createMetricsColorSettings(),
    indexes = createIndexesColorSettings(),
    ...rest
}: Partial<ICustomizableColor> = {}): ICustomizableColor;

function createPaginationButtonStateSettings({
    background = '',
    font = createFont()
}: Partial<IPaginationButtonStateProps> = {}): IPaginationButtonStateProps;

function createPaginationSettings({
    active = createPaginationButtonStateSettings(),
    base = createPaginationButtonStateSettings(),
    height = '',
    width = '',
    position = '',
    gap = '',
    pages = 0,
    borderRadius = createBorderRadius(),
    ...rest
}: Partial<IPaginationProps> = {}): IPaginationProps;

function isValidColor(value: string): boolean;

export {
    ControlMixin,
    ObjectControlMixin,
    LabelMixin,
    getConstants,
    getConstantsHash,
    isConstant,
    isValidColor,
    createFont,
    createBorder,
    createBorderRadius,
    createShadow,
    createCustomizableColor,
    createPaginationSettings
};
