<template>
    <div class="d-flex flex-center w-100 h-100 text-muted" :class="cssClass">
        <ui-group :gap="groupGap" :type="type" justify="center" align="center">
            <ui-icon :name="icon" :size="iconSize" v-if="icon"></ui-icon>
            <slot name="message">
                <span :class="labelCssClass">
                    <!--
                @slot content slot
                @example Lorem ipsum
                -->
                    <slot></slot>
                </span>
            </slot>
        </ui-group>
    </div>
</template>
<script>
/* eslint-disable no-restricted-syntax */
import UiIcon from './Icon.vue';
import UiGroup from './layout/Group.vue';

const Size = {
    NORMAL: { name: 'normal', class: [] },
    SMALL: { name: 'small', class: [] }
};

const Type = {
    HORIZONTAL: 'horizontal',
    VERTICAL: 'vertical'
};


export default {
    components: { UiGroup, UiIcon },
    props: {
        type: {
            type: String,
            default: Type.HORIZONTAL,
            validator: (type) => Object.values(Type).includes(type),
        },
        /**
         * @example account
         */
        icon: {
            type: String,
            default: ''
        },
        /**
         * @default normal
         * @values small,normal
         */
        size: {
            type: String,
            default: Size.NORMAL.name,
            validator: (val) =>
                Object.values(Size)
                    .map(({ name }) => name)
                    .includes(val)
        }
    },
    computed: {
        /**
         * @return {object}
         */
        cssClass() {
            const { size } = this;
            const sizeDef = Object.values(Size).find(({ name }) => name === size);
            const classes = [...sizeDef.class];
            return classes;
        },
        /**
         * @return {string}
         */
        groupGap() {
            const { size } = this;
            return size === Size.NORMAL.name ? 'normal' : 'small';
        },
        /**
         * @return {string}
         */
        iconSize() {
            const { size } = this;
            return size === Size.NORMAL.name ? 'xlarge' : 'large';
        },
        /**
         * @return {string[]|object}
         */
        labelCssClass() {
            const { size } = this;
            return size === Size.NORMAL.name ? ['text-large', 'text-bold'] : [];
        }
    }
};
</script>
