<template>
    <div class="preloader" :class="cssClass"></div>
</template>
<script>
const Size = {
    SMALL: { name: 'small', class: [] },
    NORMAL: { name: 'normal', class: [] },
    LARGE: { name: 'large', class: ['pad-4'] },
    XLARGE: { name: 'xlarge', class: ['pad-l1'] }
};

export default {
    props: {
        /**
         * @default normal
         * @values normal,large,xlarge
         */
        size: {
            type: String,
            default: Size.NORMAL.name,
            validator: (val) =>
                Object.values(Size)
                    .map(({ name }) => name)
                    .includes(val)
        }
    },
    computed: {
        cssClass() {
            const { size } = this;
            const sizeDef = Object.values(Size).find((def) => def.name === size);
            const classes = [...sizeDef.class];
            return classes;
        }
    }
};
</script>
