export interface IColorManagerHashValue {
    id: string;
    name: string;
    value: string;
}

export interface IColorManagerHash extends Record<string, IColorManagerHashValue> {}

export class ColorManager {
    /**
     * @return {ColorManager}
     */
    static instance: ColorManager;
    
    static isConstant(value: string): boolean;

    private _hash: IColorManagerHash = {};

    /**
     * Constructor
     *
     * @param {symbol} enforcer  singleton enforcer
     */
    constructor(enforcer: symbol);

    private _constants: {};
    
    /**
     * @param {string} value
     * @return {string}
     */
    interpolate(value: string): string;
    
    /**
     * Return key value or key if no value found
     *
     * @param {string} key  key
     * @return {any} value
     */
    getValue(key: string): any;

    /**
     * Set hash
     *
     * @param {IColorManagerHash} [hash]
     */
    setHash(hash?: IColorManagerHash): void;

    /**
     * Return hash
     *
     * @return {IColorManagerHash}
     */
    getHash(): IColorManagerHash;
}
