export interface IConstManagerHash extends Record<string, string> {}

export class ConstManager {
    /**
     * @return {ConstManager}
     */
    static instance: ConstManager;
    
    static isConstant(value: string): boolean;

    /**
     * Constructor
     *
     * @param {symbol} enforcer  singleton enforcer
     */
    constructor(enforcer: symbol);

    private _constants: IConstManagerHash;

    /**
     * @param {string} value
     * @return {string}
     */
    interpolate(value: string): string;
    
    /**
     * Return key value or key if no value found
     *
     * @param {string} key  key
     * @return {any} value
     */
    getConstValue(key: string): any;

    /**
     * Set the constants hash
     *
     * @param {Record<string, any>} constants
     */
    setConstantsHash(constants?: IConstManagerHash): void;

    /**
     * Return the constants hash
     *
     * @return {Record<string, any>}
     */
    getConstantsHash(): IConstManagerHash;
}
