<template>
    <div class="badge color-body u-select-none text-truncate" :class="cssClass" :title="description || text">
        <span class="text-truncate">{{ text }}</span>
    </div>
</template>

<script>
const BadgeClassMap = {
    'default': ['badge--default']
};

export default {
    name: 'Badge',
    props: {
        text: {
            type: String,
            default: ''
        },
        description: {
            type: String,
            default: ''
        },
        type: {
            type: String,
            default: 'default'
        }
    },
    computed: {
        cssClass() {
            return BadgeClassMap[this.type];
        }
    }
};
</script>
<style scoped>
@b badge {
    font-size: 0.8rem;
    @m default {
        border: 1px var(--color-muted) solid;
        background-color: transparent;
    }
}
</style>
