<template>
    <div id="app" class="pad-l1 w-100">
        <ui-select
            class="block"
            v-model="demoActive"
            :options="demos"
            placeholder="Select component"
        />
        <component :is="demoComponent" v-if="demoComponent"></component>
    </div>
</template>

<script>
import UiSelect from './components/ui/Select.vue';
import * as Exports from './../index';

const { Utils, Constants, ...Components } = Exports;

export default {
    name: 'App',
    components: {
        UiSelect,
    },
    data() {
        return {
            demoActive: null,
            demos: Object.keys(Components).map(i => {
                return { value: i, label: i };
            }),
        };
    },
    computed: {
        demoComponent() {
            return this.demoActive ? Components[this.demoActive] : null;
        },
    },
};
</script>
