<template>
    <div class="ui-color-picker-saturation" @mousedown.prevent.stop="selectSaturation">
        <canvas ref="canvasSaturation" />
        <div class="slide" :style="slideSaturationStyle" />
    </div>
</template>
<style lang="less" scoped>
.ui-color-picker-saturation {
    position: relative;
    cursor: pointer;
    .slide {
        position: absolute;
        left: 100px;
        top: 0;
        width: 10px;
        height: 10px;
        border-radius: 50%;
        border: 1px solid #fff;
        box-shadow: 0 0 1px 1px rgba(0, 0, 0, 0.3);
        pointer-events: none;
    }
}
</style>
<script>
import { createLinearGradient } from './utils';
export default {
    props: {
        color: {
            type: String,
            default: '#000000',
        },
        hsv: {
            type: Object,
            default: null,
        },
        size: {
            type: Number,
            default: 150,
        },
    },
    data() {
        return {
            slideSaturationStyle: {},
        };
    },
    created() {
        this._color = { r: 0, g: 0, b: 0 };
    },
    mounted() {
        this.renderColor();
        this.renderSlide();
    },
    methods: {
        renderColor() {
            const canvas = this.$refs.canvasSaturation;
            const size = this.size;
            const ctx = canvas.getContext('2d');
            canvas.width = size;
            canvas.height = size;

            ctx.fillStyle = this.color;
            ctx.fillRect(0, 0, size, size);

            createLinearGradient('l', ctx, size, size, '#FFFFFF', 'rgba(255,255,255,0)');
            createLinearGradient('p', ctx, size, size, 'rgba(0,0,0,0)', '#000000');
        },
        renderSlide() {
            this.slideSaturationStyle = {
                left: this.hsv.s * this.size - 5 + 'px',
                top: (1 - this.hsv.v) * this.size - 5 + 'px',
            };
        },
        selectSaturation(e) {
            const { top: saturationTop, left: saturationLeft } = this.$el.getBoundingClientRect();
            const ctx = e.target.getContext('2d');

            const mousemove = e => {
                let x = e.clientX - saturationLeft;
                let y = e.clientY - saturationTop;

                if (x < 0) {
                    x = 0;
                }
                if (y < 0) {
                    y = 0;
                }
                if (x > this.size) {
                    x = this.size;
                }
                if (y > this.size) {
                    y = this.size;
                }

                this.slideSaturationStyle = {
                    left: x - 5 + 'px',
                    top: y - 5 + 'px',
                };
                const imgData = ctx.getImageData(
                    Math.min(x, this.size - 1),
                    Math.min(y, this.size - 1),
                    1,
                    1
                );
                const [r, g, b] = imgData.data;
                this._color = { r, g, b };
                this.$emit('changeSaturation', this._color);
            };

            mousemove(e);

            const mouseup = () => {
                document.removeEventListener('mousemove', mousemove);
                document.removeEventListener('mouseup', mouseup);
                this.$emit('selectSaturation', this._color);
            };

            document.addEventListener('mousemove', mousemove);
            document.addEventListener('mouseup', mouseup);
        },
    },
};
</script>
