import {
    Button,
    Select,
    InputUnits,
    ColorPicker,
    ToolbarPopover,
    InputAuto,
    Link,
    Image
} from './renders';

export type WysiwygRender = Readonly<{
    BUTTON: Button;
    COLOR_PICKER: ColorPicker;
    INPUT_UNITS: InputUnits;
    SELECT: Select;
    TOOLBAR_POPOVER: ToolbarPopover;
    INPUT_AUTO: InputAuto;
    LINK: Link;
    IMAGE: Image;
}>;

export interface ICommand {
    name: String;
    title: String;
    exec: () => void;
    isActive: () => Boolean;
    isEnabled: () => Boolean;
}

export interface ITool {
    name: String;
    render: Render;
    title: String;
    icon: String;
    exec: (value?: String | Record<String, any>) => void;
    isActive: () => Boolean;
    isEnabled: () => Boolean;
    getValue?: () => String | Record<String, any>;
    options?: Array<String | ITool | ICommand>;
    units?: Array<String>
}

